import React from 'react';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import { Container } from 'react-bootstrap';
import { Button } from 'reactstrap';
import {ToastContainer,toast} from 'react-toastify';

const btnHandle = () =>{
    toast.success("Done", {
      position:'top-center'
    });
  }

  const btnHandle2 = () =>{
    toast.success("Cleared", {
      position:'top-center'
    });
  }
 const AddCourse=()=>{
    return(
        <>

<h2 className="text-center">Fill out the course details</h2>
       <Form>
       <Form.Label>Email address</Form.Label>
       <Form.Control type="email" placeholder="Enter email"  />
       
       <Form.Label>Course ID</Form.Label>
       <Form.Control type="email" placeholder="Enter  Course Id"  />

       <Form.Label>Course Title</Form.Label>
       <Form.Control type="email" placeholder="Enter Title"  />
       <br/>
        
      <FloatingLabel controlId="floatingTextarea" label="Course Description"  className="mb-3">
      <Form.Control as="textarea" placeholder="Write Course Description" /></FloatingLabel>



      <Container className="text-center">
      <Button onClick={btnHandle} color="dark" >Done </Button>
      <Button onClick={btnHandle2} color="warning ml-2" style={{marginLeft:'5px'}} >Clear </Button>
      </Container>
      </Form>

        </>
    )
 }
 export default AddCourse;
